sp_widget.client_script:
function() {
  /* widget controller */
	var c = this;

	c.selectItem = function(index){
		var id = c.data.notes[index].sys_id;
		console.log("Note ID was selected: " + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}

	$rootScope.$on('updateNote', function(event, data){
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function(result){
			var index = c.data.notes.indexOf(function(note){ return note.sys_id == result.data.note.noteID });
			c.data.notes.splice(index, 1, 
													{title: result.data.note.title,
													note: result.data.note.note,
													sys_id: result.data.note.sys_id
													});
		});
	});
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */


	if(input){
		if(input.action == 'getNote'){
			var updatedNote = new GlideRecord('x_snc_createnotes_note');
			updatedNote.get('sys_id', input.noteID);
			data.note = {};
			$sp.getRecordDisplayValues(data.note, updatedNote, 'number,title, sys_id');
			var updatedNoteText = updatedNote.getValue('note').slice(0, 20);
			data.note.note = updatedNoteText.length == 20 ? updatedNoteText + '...' : updatedNoteText;
			return;
		}
	}

	data.notes = [];
	var notesRecord = new GlideRecord('x_snc_createnotes_note');
	notesRecord.addQuery('user', gs.getUser().getID());
	notesRecord.orderByDesc('sys_created_on');
	notesRecord.query();
	while(notesRecord.next()){
		var note = {};
		$sp.getRecordDisplayValues(note, notesRecord, 'number,title,sys_id');
		var noteText = notesRecord.getValue('note').slice(0, 20);
		note.note = noteText.length == 20 ? noteText + '...' : noteText;

		data.notes.push(note);
	}
})();
